mapper.select:
select * from goods


        and name like concat('%', #{name}, '%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc

mapper.update:
update goods
name = #{name}, category_id = #{categoryId}, price = #{price}, description = #{description}, images = #{images}, status = #{status}, stock = #{stock}, update_time = #{updateTime},
where id = #{id}
